$(document).ready(function(){$(".jsSelectAllInGroup").on("click",function(a){a.preventDefault();$(this).closest(".permissionGroup").find("input[type=checkbox]").each(function(b,c){$(c).iCheck("check")})});$(".jsDeselectAllInGroup").on("click",function(a){a.preventDefault();$(this).closest(".permissionGroup").find("input[type=checkbox]").each(function(b,c){$(c).iCheck("uncheck")})})});